// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "./utils.sol";

contract Crowdfunding {
    address public platformOwner;
    uint256 private fee;

    using Utils for *;

    struct Campaign {
        uint256 Id;
    }

    struct Donative {
        address donner;
        uint256 quantity;
    }

    // stiore the campaigs
    mapping(uint256 => Campaign) private campaigns;

    // store the owner campaigns
    mapping(address => uint256[]) private ownerCampaigns;

    // store the donatives history for a campaign
    mapping(uint256 => Donative[]) private donatives;

    constructor() {
        fee = 3;
        platformOwner = msg.sender;
    }

    function getDonatives(uint256 campaignId) public view returns (Donative[] memory) {
        return donatives[campaignId];
    }

    function getCampaignInfo(uint256 campaignId) public view returns (Campaign memory) {
        return campaigns[campaignId];
    }
}

